import React from "react";
import { Login, Logo } from "../assets/icon";
import { Link } from "react-router-dom";

function LoginPage() {
  return (
	<div className="container-fluid vh-100">
	<div className="row h-100">
	  {/* Bagian Form */}
	  <div className="col-md-6 d-flex align-items-center justify-content-center">
	    <div style={{ maxWidth: '350px', width: '100%' }}>
		 {/* Logo dan Judul */}
		 <div className="d-flex align-items-center justify-content-center mb-4">
		   <img src={Logo} className="img-fluid me-2" width="32" alt="Logo" />
		   <h4 className="mb-0">SIMS PPOB</h4>
		 </div>
		 
		 {/* Judul Form */}
		 <h3 className="text-center mb-3">Masuk atau buat akun untuk memulai</h3>
		 
		 {/* Input Email */}
		 <div className="form-control mb-3 d-flex align-items-center">
		   <i className="fa fa-at me-2 text-secondary"></i>
		   <input
			type="email"
			placeholder="masukkan email anda"
			style={{
			  border: "none",
			  width: "100%",
			  outline: "none",
			  boxShadow: "none",
			}}
		   />
		 </div>
		 
		 {/* Input Password */}
		 <div className="form-control mb-3 d-flex align-items-center">
		   <i className="fa fa-lock me-2 text-secondary"></i>
		   <input
			type="password"
			placeholder="masukkan password anda"
			style={{
			  border: "none",
			  width: "100%",
			  outline: "none",
			  boxShadow: "none",
			}}
		   />
		   <i className="fa fa-eye ms-2 text-secondary"></i>
		 </div>
		 
		 {/* Tombol Masuk */}
		 <button className="my-4 btn btn-danger w-100">Masuk</button>
		 
		 {/* Teks Registrasi */}
		 <small className="text-center d-block text-secondary">
		   belum punya akun? registrasi{" "}
		   <Link
			to="/daftar"
			className="text-danger"
			style={{ textDecoration: "none" }}
		   >
			di sini
		   </Link>
		 </small>
	    </div>
	  </div>
	  
	  {/* Bagian Gambar */}
	  <div className="col-md-6">
	    <img
		 className="img-fluid w-100 h-100"
		 src={Login}
		 alt="Login Illustration"
		 style={{ objectFit: "cover", backgroundColor: "#f8f0f4" }}
	    />
	  </div>
	</div>
   </div>
   
  );
}

export default LoginPage;
