import React, { useState } from "react";
import { Login, Logo } from "../assets/icon";
import { Link } from "react-router-dom";
import { InputField } from "../components";

function LoginPage() {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const [showPassword, setShowPassword] = useState(false);

  const setPasswordVisibility = () => {
    setVisible(!visible);
  };
  return (
    <div className="container-fluid vh-100">
      <div className="row h-100">
        <div className="col-md-6 d-flex align-items-center justify-content-center">
          <div className="container-fluid px-5 mx-5">
            <div className="d-flex align-items-center justify-content-center mb-4">
              <img
                src={Logo}
                className="img-fluid me-2"
                width="32"
                alt="Logo"
              />
              <h4 className="mb-0">SIMS PPOB</h4>
            </div>

            <h2 className="text-center mb-4">
              Masuk atau buat akun untuk memulai
            </h2>

            <InputField
              type="email"
              placeholder="masukkan email anda"
              iconClass="fa fa-at"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <InputField
              type={visible ? 'text' : 'password'}
              placeholder="masukkan password anda"
              iconClass="fa fa-lock"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              seePassword={setPasswordVisibility}
            />

            <button className="my-4 btn btn-danger w-100">Masuk</button>

            <small className="text-center d-block text-secondary">
              belum punya akun? registrasi{" "}
              <Link
                to="/registrationPage"
                className="text-danger"
                style={{ textDecoration: "none" }}
              >
                di sini
              </Link>
            </small>
          </div>
        </div>

        <div className="col-md-6 d-none d-md-block p-0">
          <img
            className="img-fluid w-100 h-100"
            src={Login}
            alt="Login Illustration"
            style={{ objectFit: "cover", backgroundColor: "#f8f0f4" }}
          />
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
