import {
     UPDATE_PROFILE_IMAGE,
     UPDATE_PROFILE_IMAGE_SUCCESS,
     UPDATE_PROFILE_IMAGE_FAILURE,
     LOGOUT,
     LOGIN,
     LOGIN_SUCCESS,
     LOGIN_FAILURE,
     GET_SALDO,
     GET_SALDO_SUCCESS,
     GET_SALDO_FAILURE,
     GET_PROFILE,
     GET_PROFILE_SUCCESS,
     GET_PROFILE_FAILURE,
} from "../types/actionTypes";

const initialState = {
     token: null,
     profile: {
          namaDepan: "",
          namaBelakang: "",
          email: "",
          profilePicture: "",
     },
     saldo: 0,
     loading: false,
     error: null,
};

const authReducer = (state = initialState, action) => {
     switch (action.type) {
          case LOGIN:
          case GET_PROFILE:
          case GET_SALDO:
          case UPDATE_PROFILE_IMAGE:
               return { ...state, loading: true, error: null };



          case GET_PROFILE_SUCCESS:
               return {
                    ...state,
                    loading: false,
                    profile: action.payload,
                    token: action.payload.token || state.token,
                    error: null,
               };

          case GET_PROFILE_FAILURE:
               return { ...state, loading: false, error: action.error };

          case GET_SALDO_SUCCESS:
               return {
                    ...state,
                    loading: false,
                    saldo: action.payload.saldo,
                    error: null,
               };

          case GET_SALDO_FAILURE:
               return { ...state, loading: false, error: action.error };

          case LOGOUT:
               return { ...initialState };

          default:
               return state;
     }
};

export default authReducer;
