import React from "react";

function InputField({
  type,
  placeholder,
  iconClass,
  value,
  onChange,
  togglePassword,
  showPassword,
  isInvalid,
  errorMessage,
}) {
  console.log("InputField - isInvalid:", isInvalid); // Debugging: lihat nilai isInvalid
  console.log("InputField - errorMessage:", errorMessage); // Debugging: lihat nilai errorMessage

  return (
    <div className="mb-3">
      <div
        className="form-control d-flex align-items-center"
        style={{
          borderColor: isInvalid ? "#ff3b3b" : "inherit",
        }}
      >
        <i
          className={`${iconClass} me-3`}
          style={{ color: isInvalid ? "#ff3b3b" : "inherit" }}
        ></i>
        <input
          type={type}
          placeholder={placeholder}
          value={value}
          onChange={onChange}
          style={{
            border: "none",
            width: "100%",
            outline: "none",
            boxShadow: "none",
            color: isInvalid ? "#ff3b3b" : "inherit",
          }}
        />
        {type === "password" && togglePassword && (
          <i
            className={`fa ${showPassword ? "fa-eye-slash" : "fa-eye"} ms-2`}
            onClick={togglePassword}
            style={{
              cursor: "pointer",
              color: isInvalid ? "#ff3b3b" : "inherit",
            }}
          ></i>
        )}
      </div>
      {isInvalid && errorMessage && (
        <div
          style={{
            color: "#ff3b3b",
            fontSize: "0.85em",
            textAlign: "right",
            marginTop: "4px",
          }}
        >
          {errorMessage}
        </div>
      )}
    </div>
  );
}

export default InputField;
