import { createSlice } from '@reduxjs/toolkit';

const authSlice = createSlice({
     name: 'auth',
     initialState: {
          token: localStorage.getItem("token") || null,
          profile: { email: '', namaDepan: '', namaBelakang: '', profilePicture: '' },
          saldo: 0,
     },
     reducers: {
          login: (state, action) => {
               state.token = action.payload;
          },
          logout: (state) => {
               state.token = null;
               state.profile = { email: '', namaDepan: '', namaBelakang: '', profilePicture: '' };
               state.saldo = 0;
          },
          setProfile: (state, action) => {
               state.profile = action.payload;
          },
          setSaldo: (state, action) => {
               state.saldo = action.payload;
          },
     },
});

export const { login, logout, setProfile, setSaldo } = authSlice.actions;
export default authSlice.reducer;
