import React, { useState, useContext, useEffect } from "react";
import { Header, InputField } from "../components";
import { AuthContext } from "../contexts/AuthContext";
import axios from "axios";
import { baseUrl } from "../api";

function AccountPage() {
  const { token, logout, profile, loadData } = useContext(AuthContext);
  const [namaDepan, setNamaDepan] = useState("");
  const [namaBelakang, setNamaBelakang] = useState("");
  const [emailForm, setEmailForm] = useState("");
  const [namaDepanForm, setNamaDepanForm] = useState("");
  const [namaBelakangForm, setNamaBelakangForm] = useState("");
  const [profilePicture, setProfilePicture] = useState("");
  const [isEdit, setIsEdit] = useState(false);
  const [loading, setLoading] = useState(false);
  const [showErrorModal, setShowErrorModal] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const isFormValid = namaDepanForm === "" || namaBelakangForm === "";

  useEffect(() => {
    const loadProfile = () => {
      setLoading(true);
      try {
        setNamaDepan(profile.namaDepan);
        setNamaBelakang(profile.namaBelakang);
        setProfilePicture(profile.profilePicture);
        setEmailForm(profile.email);
        setNamaDepanForm(profile.namaDepan);
        setNamaBelakangForm(profile.namaBelakang);
      } catch (error) {
        console.log(error);
      } finally {
        setLoading(false);
      }
    };

    loadProfile();
  }, [profile]);

  const saveProfile = async () => {
    setLoading(true);
    try {
      await axios.put(
        `${baseUrl}profile/update`,
        {
          first_name: namaDepanForm,
          last_name: namaBelakangForm,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      loadData();
      setIsEdit(false);
    } catch (error) {
      console.error(error);
      setErrorMessage("Gagal memperbarui profil.");
      setShowErrorModal(true);
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (file) {
      if (file.size > 100 * 1024) {
        setShowErrorModal(true)
        setErrorMessage("Ukuran file melebihi 100 KB.");
        return;
      }
      const imageData = new FormData();
      imageData.append("file", file);

      try {
        setLoading(true);
        await axios.put(`${baseUrl}profile/image`, imageData, {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data",
          },
        });
        loadData();
      } catch (error) {
        showErrorModal(true)
        setErrorMessage("Gagal memperbarui foto profil.");
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="container-fluid vh-100">
      <Header />
      <div className="container px-5 mx-5 mt-5 d-flex flex-column justify-content-center align-items-center">
        <div className="position-relative mb-3">
          <img
            src={profilePicture}
            alt="Profile"
            className="img-fluid"
            width="100"
            style={{ borderRadius: "50%", maxWidth: 100 }}
          />
          <div
            className="position-absolute bg-white d-flex align-items-center justify-content-center"
            style={{
              bottom: 0,
              right: 0,
              width: "25px",
              height: "25px",
              borderRadius: "50%",
              border: "1px solid #ccc",
              cursor: "pointer",
            }}
          >
            <i className="fa fa-pencil"></i>
            <input
              type="file"
              accept=".jpg,.jpeg,.png"
              onChange={handleFileChange}
              style={{
                opacity: 0,
                position: "absolute",
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                cursor: "pointer",
              }}
            />
          </div>
        </div>
        <h2>{`${namaDepan} ${namaBelakang}`}</h2>
        <div className="w-50">
          <label className="form-label text-left">Email</label>
          <InputField
            iconClass="fa fa-at"
            type="email"
            value={emailForm}
            onChange={(e) => setEmailForm(e.target.value)}
            placeholder="masukkan email anda"
            style={{ width: "100%" }}
            isEdit={!false}
          />
        </div>
        <div className="w-50">
          <label className="form-label text-left">Nama Depan</label>
          <InputField
            iconClass="fa fa-user"
            type="text"
            value={namaDepanForm}
            onChange={(e) => setNamaDepanForm(e.target.value)}
            placeholder="masukkan nama depan"
            style={{ width: "100%" }}
            isEdit={!isEdit}
            isValid={isFormValid}
            errorMessage={isFormValid ? "Form tidak boleh kosong" : ""}
          />
        </div>
        <div className="w-50">
          <label className="form-label text-left">Nama Belakang</label>
          <InputField
            iconClass="fa fa-user"
            type="text"
            value={namaBelakangForm}
            onChange={(e) => setNamaBelakangForm(e.target.value)}
            placeholder="masukkan nama belakang"
            style={{ width: "100%" }}
            isEdit={!isEdit}
            isValid={isFormValid}
            errorMessage={isFormValid ? "Form tidak boleh kosong" : ""}
          />
        </div>
        <div className="w-50">
          {!isEdit ? (
            <>
              <button
                className="btn btn-danger mb-4 w-100"
                onClick={() => setIsEdit(true)}
              >
                Edit Profil
              </button>
              <button className="btn btn-outline-danger w-100" onClick={logout}>
                Logout
              </button>
            </>
          ) : (
            <>
              <button
                className="btn btn-danger mb-4 w-100"
                onClick={saveProfile}
                disabled={isFormValid}
              >
                Simpan
              </button>
              <button
                className="btn btn-outline-danger mb-4 w-100"
                onClick={() => {
                  setIsEdit(false);
                  setNamaDepanForm(namaDepan);
                  setNamaBelakangForm(namaBelakang);
                }}
              >
                Batalkan
              </button>
            </>
          )}
        </div>
        {showErrorModal && (
          <div className="modal show d-block">
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">Error</h5>
                  <button
                    type="button"
                    className="btn-close"
                    onClick={() => setShowErrorModal(false)}
                  ></button>
                </div>
                <div className="modal-body">
                  <p>{errorMessage}</p>
                </div>
                <div className="modal-footer">
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => setShowErrorModal(false)}
                  >
                    Close
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
      {loading && (
        <div className="loading-modal">
          <div className="loading-spinner">
            <div className="spinner-border text-danger" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p>Logging in...</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default AccountPage;
