import React from "react";

function InputField({
  type,
  placeholder,
  iconClass,
  value,
  onChange,
  togglePassword,
  showPassword,
  isValid,
  errorMessage,
}) {
     console.log("InputField - isInvalid:", isInvalid); // Debugging: lihat nilai isInvalid
  console.log("InputField - errorMessage:", errorMessage);
  return (
    <div className="mb-4">
      <div
        className="form-control d-flex align-items-center"
        style={{ borderColor: isValid ? "#ff3b3b" : "inherit" }}
      >
        <i
          className={`${iconClass} me-3 text-secondary`}
          style={{ color: isValid ? "#ff3b3b" : "inherit" }}
        ></i>
        <input
          type={type}
          placeholder={placeholder}
          value={value}
          onChange={onChange}
          style={{
            border: "none",
            width: "100%",
            outline: "none",
            boxShadow: "none",
          }}
        />
        {togglePassword && (
          <i
            className={`fa ${
              showPassword ? "fa-eye-slash" : "fa-eye"
            } ms-2 text-secondary`}
            onClick={togglePassword}
            style={{ cursor: "pointer" }}
          ></i>
        )}
      </div>
      {isValid && errorMessage && (
        <div
          style={{
            color: "#ff3b3b",
            fontSize: "0.85em",
            textAlign: "right",
            marginTop: "4px",
          }}
        >
          {errorMessage}
        </div>
      )}
    </div>
  );
}

export default InputField;
