import React, { useState } from "react";
import { Header, InfoUserSaldo, InputField } from "../components";
import { useLocation } from "react-router-dom";

function PurchasePage() {
  const location = useLocation()
  const { icons, textIcon, serviceFee, serviceCode } = location.state || {}
  const [purchase, setPurchase] = useState(0);



  return (
    <div className="container-fluid vh-100">
      <Header />
      <div className="container mt-5">
        <InfoUserSaldo />
        <div className="mt-5 container-fluid w-100 p-0">
          <h4 className="mb-3">Pembayaran</h4>
          <div className="d-flex gap-3 mb-5 align-items-center">
            <img src={icons} className="img-fluid" alt={textIcon} style={{maxWidth: 30}} /> <h5>{textIcon}</h5>
          </div>
          <div className="w-100">
            <div className="mb-3">
              <InputField
                type="text"
                iconClass="fa fa-money"
                value={purchase ? parseInt(purchase).toLocaleString() : ""}
                onChange={handleInputChange}
                placeholder="masukkan nominal pembayaran"
                isValid={!validation}
                errorMessage={!validation ? "Pem" : ""}
              />
            </div>
            <button className="btn btn-danger w-100" disabled={!validation}>
              Top Up
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PurchasePage;
