import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AccountPage, HomePage, LoginPage, PurchasePage, RegistrationPage, TopUpPage, TransaksiPage } from './pages';
import { PrivatePages } from './components';
import { AuthProvider } from './contexts/AuthContext';

function App() {
  const token = localStorage.getItem("token")
  return (
    <Router>
      <AuthProvider>
        <Routes>
          <Route
            path='/'
            element={token ? <Navigate to="/homePage"/> : <LoginPage />}
          />
          <Route
            path='/registrationPage'
            element={token ? <Navigate to="/homePage"/> : <RegistrationPage />}
          />
          <Route
            path='/homePage'
            element={
              <PrivatePages>
                <HomePage />
              </PrivatePages>
            }
          />
          <Route
            path='/accountPage'
            element={
              <PrivatePages>
                <AccountPage />
              </PrivatePages>
            }
          />
          <Route
            path='/purchasePage'
            element={
              <PrivatePages>
                <PurchasePage />
              </PrivatePages>
            }
          />
          <Route
            path='/topUpPage'
            element={
              <PrivatePages>
                <TopUpPage />
              </PrivatePages>
            }
          />
          <Route
            path='/transaksiPage'
            element={
              <PrivatePages>
                <TransaksiPage />
              </PrivatePages>
            }
          />
        </Routes>
      </AuthProvider>
    </Router>
  );
}

export default App;
