import React from "react";
import InputField from "./InputField";
import { Link } from "react-router-dom";
import { Logo } from "../assets/icon";

function AuthForm({ textHeader, smallText, route, btnText }) {
  return (
    <div className="container-fluid px-5 mx-5">
      <div className="d-flex align-items-center justify-content-center mb-4">
        <img src={Logo} className="img-fluid me-2" width="32" alt="Logo" />
        <h4 className="mb-0">SIMS PPOB</h4>
      </div>

      <h2 className="text-center mb-4">Masuk atau buat akun untuk memulai</h2>

      <InputField
        type="email"
        placeholder="masukkan email anda"
        iconClass="fa fa-at"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <InputField
        type={showPassword ? "text" : "password"}
        placeholder="masukkan password anda"
        iconClass="fa fa-lock"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        togglePassword={togglePassword}
        showPassword={showPassword}
      />

      <button className="my-4 btn btn-danger w-100">Masuk</button>

      <small className="text-center d-block text-secondary">
        belum punya akun? registrasi
        <Link
          to="/registrationPage"
          className="text-danger"
          style={{ textDecoration: "none" }}
        >
          di sini
        </Link>
      </small>
    </div>
  );
}

export default AuthForm;
