import React from "react";

function InputField({
  type,
  placeholder,
  iconClass,
  value,
  onChange,
  togglePassword,
  showPassword,
  isValid,
  errorMessage
}) {
  return (
    <div
      className="form-control mb-3 d-flex align-items-center"
      style={{ borderColor: isValid ? "#ff3b3b" : "inherit" }}
    >
      <i
        className={`${iconClass} me-3 text-secondary`}
        style={{ color: isValid ? "#ff3b3b" : "inherit" }}
      ></i>
      <input
        type={type}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        style={{
          border: "none",
          width: "100%",
          outline: "none",
          boxShadow: "none",
        }}
      />
      {togglePassword && (
        <i
          className={`fa ${
            showPassword ? "fa-eye-slash" : "fa-eye"
          } ms-2 text-secondary`}
          onClick={togglePassword}
          style={{ cursor: "pointer" }}
        ></i>
      )}
    </div>
  );
}

export default InputField;
