import React, { useContext, useState } from "react";
import { Header, InfoUserSaldo, InputField } from "../components";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { baseUrl } from "../api";
import { AuthContext } from "../contexts/AuthContext";

function PurchasePage() {
  const { token } = useContext(AuthContext);
  const location = useLocation();
  const { icons, textIcon, serviceFee, serviceCode } = location.state || {};
  const [purchase, setPurchase] = useState(0);
  const [loading, setLoading] = useState(false);
  const [purchaseStatus, setPurchaseStatus] = useState(0);
  const [confirmPurchase, setConfirmPurchase] = useState(false);

  const handleInputChange = (e) => {
    const value = e.target.value.replace(/[^0-9]/g, "");
    setPurchase(value);
  };

  const validation = parseInt(purchase) >= 1000;

  const purchaseAction = async () => {
    setConfirmPurchase(true);
    if (!confirmPurchase && loading) {
      try {
        const response = await axios.post(
          `${baseUrl}transaction`,
          {
            service_code: serviceCode,
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (response.data.status === 0) {
          setPurchaseStatus(1);
        }
      } catch (error) {
        setPurchaseStatus(2);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="container-fluid vh-100">
      <Header />
      <div className="container mt-5">
        <InfoUserSaldo />
        <div className="mt-5 container-fluid w-100 p-0">
          <h4 className="mb-3">Pembayaran</h4>
          <div className="d-flex gap-3 mb-5 align-items-center">
            <img
              src={icons}
              className="img-fluid"
              alt={textIcon}
              style={{ maxWidth: 30 }}
            />{" "}
            <h5>{textIcon}</h5>
          </div>
          <div className="w-100">
            <div className="mb-3">
              <InputField
                type="text"
                iconClass="fa fa-money"
                value={serviceFee ? parseInt(serviceFee).toLocaleString() : ""}
                onChange={handleInputChange}
                placeholder="masukkan nominal pembayaran"
                isValid={validation}
                errorMessage={validation ? "Pem" : ""}
              />
            </div>
            <button
              className="btn btn-danger w-100"
              disabled={validation}
              onClick={purchaseAction}
            >
              Top Up
            </button>
          </div>
        </div>
      </div>
      {(loading || confirmPurchase || purchaseStatus) && (
        <div className="loading-modal">
          <div className="loading-spinner">
            {loading && (
              <>
                <div className="spinner-border text-danger" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
                <p>Your purchase is processing...</p>
              </>
            )}
            {confirmPurchase && (
              <div className="d-flex flex-column align-items-center p-4">
                <i
                  className="fa fa-credit-card-alt text-white bg-danger p-3 mb-3"
                  style={{ borderRadius: "50%" }}
                ></i>
                <small className="text-secondary">
                  Bayar {textIcon} senilai
                </small>
                <h5 className="mb-4">
                  Rp. {serviceFee.toLocaleString("id-ID")} ?
                </h5>
                <div className="gap-3 d-flex flex-column">
                  <h6
                    className="text-danger small"
                    style={{ cursor: "pointer" }}
                    onClick={() => {
                      setConfirmPurchase(false);
                      setLoading(true);
                    }}
                  >
                    Ya, Lanjutkan bayar
                  </h6>
                  <h6
                    className="text-secondary small"
                    style={{ cursor: "pointer" }}
                    onClick={() => setConfirmPurchase(false)}
                  >
                    Batalkan
                  </h6>
                </div>
              </div>
            )}
            {purchaseStatus === 0 && (
              <div className="d-flex flex-column align-items-center p-4">
                <i
                  className="fa fa-credit-card-alt text-white bg-success p-3 mb-3"
                  style={{ borderRadius: "50%" }}
                ></i>
                <small className="text-secondary">
                  Pembayaran {textIcon} senilai
                </small>
                <h5 className="mb-4">
                  Rp. {serviceFee.toLocaleString("id-ID")} ?
                </h5>
                <div className="gap-3 d-flex flex-column">
                  <h6
                    className="text-danger small"
                    style={{ cursor: "pointer" }}
                    onClick={() => {
                      setConfirmPurchase(false);
                      setLoading(true);
                    }}
                  >
                    Ya, Lanjutkan bayar
                  </h6>
                  <h6
                    className="text-secondary small"
                    style={{ cursor: "pointer" }}
                    onClick={() => setConfirmPurchase(false)}
                  >
                    Batalkan
                  </h6>
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default PurchasePage;
