import React, { useEffect, useState } from "react";
import { ProfilePicture, BGSaldo } from "../assets/icon";
import axios from "axios";
import { baseUrl } from "../api";

function InfoUserSaldo() {
  const [showSaldo, setShowSaldo] = useState(false);
  const [state, setState] = useState({
    email: "",
    namaDepan: "",
    namaBelakang: "",
    profilePicture: "",
  });
  let saldo = 0;

  // console.log(localStorage.getItem("token"))
  useEffect(() => {
    const loadData = async () => {
      setLoading(true);
      try {
        const response = await axios.get(baseUrl + `profile`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });

        if (response.data.status === 0) {
          setState({
            email: response.data.data.email,
            namaDepan: response.data.data.first_name,
            namaBelakang: response.data.data.last_name,
            profilePicture: response.data.data.profile_image,
          });
        }
      } catch (err) {
        const errorMessage = err.response?.data?.message || "Terjadi Kesalahan";
        console.log(errorMessage);
      } finally {
        setLoading(false);
      }
    };
    loadData();
  });

  useEffect(() => {
    const loadSaldo = async () => {
      try {
        const response = await axios.get(baseUrl + `balance`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });

        if (response.data.status === 0) {
          saldo = response.data.data.balance;
        }
      } catch (err) {
        const errorMessage = err.response?.data?.message || "Terjadi Kesalahan";
        console.log(errorMessage);
      }
    };
    loadSaldo();
  });

  const toggleSaldo = () => setShowSaldo(!showSaldo);
  const formattedValue = showSaldo ? saldo.toLocaleString() : saldo.toString();

  return (
    <div className="row">
      <div className="col-md-6">
        <img
          src={state.profilePicture}
          alt="Profile"
          className="img-fluid mb-4"
          style={{ maxWidth: 70, borderRadius: "50%" }}
        />
        <h6>Selamat Datang,</h6>
        <h2
          style={{ textTransform: "capitalize" }}
        >{`${state.namaDepan} ${state.namaBelakang}`}</h2>
      </div>
      <div className="col-md-6 position-relative d-none d-md-block p-0">
        <img
          src={BGSaldo}
          alt="Saldo Container"
          className="img-fluid w-100 h-100"
          style={{ objectFit: "fill" }}
        />
        <div
          className="position-absolute top-50 translate-middle text-white"
          style={{ zIndex: 1, left: 240 }}
        >
          <h6 className="mb-3">Saldo Anda</h6>
          <div className="d-flex flex-row">
            <h2>Rp </h2>
            <input
              type={`${showSaldo ? "text" : "password"}`}
              value={showSaldo ? formattedValue : 1234567}
              className="text-white h2 ps-2"
              style={{
                background: "none",
                border: "none",
                outline: "none",
                boxShadow: "none",
              }}
              readOnly
            />
          </div>
          <div className="d-flex flex-row align-items-center">
            <small>Lihat Saldo</small>
            <i
              className={`fa ${showSaldo ? "fa-eye-slash" : "fa-eye"} mx-3`}
              onClick={toggleSaldo}
              style={{ cursor: "pointer" }}
            ></i>
          </div>
        </div>
      </div>
    </div>
  );
}

export default InfoUserSaldo;
