import React, { useState } from "react";
import { Header, InputField } from "../components";
import { ProfilePicture } from "../assets/icon";

function AccountPage() {
  const [email, setEmail] = useState("");
  const [namaDepan, setNamaDepan] = useState("");
  const [namaBelakang, setNamaBelakang] = useState("");
  const [isEdit, setIsEdit] = useState(false);

  const editProfile = () => setIsEdit(true)
  const saveProfile = () => {
    setIs
  }
  return (
    <div className="container-fluid vh-100">
      <Header />
      <div className="container px-5 mx-5 mt-5 d-flex flex-column justify-content-center align-items-center">
        <div className="position-relative mb-3">
          <img
            src={ProfilePicture}
            alt="Profile"
            className="img-fluid"
            width="100"
            style={{ borderRadius: "50%" }}
          />
          <div
            className="position-absolute bg-white d-flex align-items-center justify-content-center"
            style={{
              bottom: 0,
              right: 0,
              width: "25px",
              height: "25px",
              borderRadius: "50%",
              border: "1px solid #ccc",
              cursor: "pointer",
            }}
          >
            <i className="fa fa-pencil"></i>
          </div>
        </div>
        <h2>Namaaaaaaa</h2>
        <div className="w-50">
          <label className="form-label text-left">Email</label>
          <InputField
            iconClass="fa fa-at"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="masukkan email anda"
            style={{ width: "100%" }}
          />
        </div>
        <div className="w-50">
          <label className="form-label text-left">Nama Depan</label>
          <InputField
            iconClass="fa fa-user"
            type="text"
            value={namaDepan}
            onChange={(e) => setNamaDepan(e.target.value)}
            placeholder="masukkan nama depan"
            style={{ width: "100%" }}
          />
        </div>
        <div className="w-50">
          <label className="form-label text-left">Nama Belakang</label>
          <InputField
            iconClass="fa fa-user"
            type="text"
            value={namaBelakang}
            onChange={(e) => setNamaBelakang(e.target.value)}
            placeholder="masukkan nama belakang"
            style={{ width: "100%" }}
          />
        </div>
        <div className="w-50">
          {!isEdit ? (
            <>
              <button className="btn btn-danger mb-4 w-100" onClick={editProfile}>Edit Profil</button>
              <button className="btn btn-outline-danger w-100">Logout</button>
            </>
          ) : (
            <button className="btn btn-danger mb-4 w-100">Simpan</button>
          )}
        </div>
      </div>
    </div>
  );
}

export default AccountPage;
