import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AccountPage, HomePage, LoginPage, PurchasePage, RegistrationPage, TopUpPage, TransaksiPage } from './pages';
import { PrivatePages } from './components';
import { AuthContext, AuthProvider } from './contexts/AuthContext';
import { useContext } from 'react';

function App() {
  return (
    <Provider>
      <Router>
        <AuthProvider>
          <AppRoutes />
        </AuthProvider>
      </Router>
    </Provider>
  );
}

function AppRoutes() {
  const { token } = useContext(AuthContext);

  return (
    <Routes>
      <Route
        path='/'
        element={token ? <Navigate to="/homePage" /> : <LoginPage />}
      />
      <Route
        path='/registrationPage'
        element={token ? <Navigate to="/homePage" /> : <RegistrationPage />}
      />
      <Route
        path='/homePage'
        element={
          <PrivatePages>
            <HomePage />
          </PrivatePages>
        }
      />
      <Route
        path='/accountPage'
        element={
          <PrivatePages>
            <AccountPage />
          </PrivatePages>
        }
      />
      <Route
        path='/purchasePage'
        element={
          <PrivatePages>
            <PurchasePage />
          </PrivatePages>
        }
      />
      <Route
        path='/topUpPage'
        element={
          <PrivatePages>
            <TopUpPage />
          </PrivatePages>
        }
      />
      <Route
        path='/transaksiPage'
        element={
          <PrivatePages>
            <TransaksiPage />
          </PrivatePages>
        }
      />
    </Routes>
  );
}

export default App;
