import React, { useContext, useState } from "react";
import { Header, InfoUserSaldo, InputField } from "../components";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import { baseUrl } from "../api";
import { AuthContext } from "../contexts/AuthContext";

function PurchasePage() {
  const { token } = useContext(AuthContext);
  const location = useLocation();
  const navigate = useNavigate();
  const { icons, textIcon, serviceFee, serviceCode } = location.state || {};
  const [purchase, setPurchase] = useState(0);
  const [loading, setLoading] = useState(false);
  const [purchaseStatus, setPurchaseStatus] = useState(null);
  const [confirmPurchase, setConfirmPurchase] = useState(false);

  const handleInputChange = (e) => {
    const value = e.target.value.replace(/[^0-9]/g, "");
    setPurchase(value);
  };

  const validation = parseInt(purchase) >= 1000;

  const purchaseAction = async () => {
    if (confirmPurchase) return;

    setConfirmPurchase(true); // Tampilkan konfirmasi pembelian
    setLoading(true); // Aktifkan loading ketika melakukan request

    try {
      const response = await axios.post(
        `${baseUrl}transaction`,
        { service_code: serviceCode },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.data.status === 0) {
        setPurchaseStatus("success");
      }
    } catch (error) {
      setPurchaseStatus("failure");
    } finally {
      setLoading(false);
      setConfirmPurchase(false);
    }
  };

  return (
    <div className="container-fluid vh-100">
      <Header />
      <div className="container mt-5">
        <InfoUserSaldo />
        <div className="mt-5 container-fluid w-100 p-0">
          <h4 className="mb-3">Pembayaran</h4>
          <div className="d-flex gap-3 mb-5 align-items-center">
            <img src={icons} className="img-fluid" alt={textIcon} style={{ maxWidth: 30 }} />
            <h5>{textIcon}</h5>
          </div>
          <div className="w-100">
            <div className="mb-3">
              <InputField
                type="text"
                iconClass="fa fa-money"
                value={serviceFee ? parseInt(serviceFee).toLocaleString() : ""}
                onChange={handleInputChange}
                placeholder="Masukkan nominal pembayaran"
                re
              />
            </div>
            <button
              className="btn btn-danger w-100"
              disabled={!validation || loading}
              onClick={purchaseAction}
            >
              Top Up
            </button>
          </div>
        </div>
      </div>

      {(loading || confirmPurchase || purchaseStatus) && (
        <Modal
          loading={loading}
          confirmPurchase={confirmPurchase}
          purchaseStatus={purchaseStatus}
          textIcon={textIcon}
          serviceFee={serviceFee}
          onConfirm={() => {
            setConfirmPurchase(false);
            setLoading(true);
          }}
          onCancel={() => {
            setConfirmPurchase(false);
            setPurchaseStatus(null);
          }}
          onNavigate={() => {
            setPurchaseStatus(null);
            navigate("/homePage");
          }}
        />
      )}
    </div>
  );
}

function Modal({ loading, confirmPurchase, purchaseStatus, textIcon, serviceFee, onConfirm, onCancel, onNavigate }) {
  if (loading) {
    return (
      <div className="loading-modal">
        <div className="loading-spinner">
          <div className="spinner-border text-danger" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Your purchase is processing...</p>
        </div>
      </div>
    );
  }

  if (confirmPurchase) {
    return (
      <div className="loading-modal">
        <div className="d-flex flex-column align-items-center p-4">
          <i className="fa fa-credit-card-alt text-white bg-danger p-3 mb-3" style={{ borderRadius: "50%" }}></i>
          <small className="text-secondary">Bayar {textIcon} senilai</small>
          <h5 className="mb-4">Rp. {serviceFee.toLocaleString("id-ID")}</h5>
          <div className="gap-3 d-flex flex-column">
            <h6 className="text-danger small" style={{ cursor: "pointer" }} onClick={onConfirm}>
              Ya, Lanjutkan bayar
            </h6>
            <h6 className="text-secondary small" style={{ cursor: "pointer" }} onClick={onCancel}>
              Batalkan
            </h6>
          </div>
        </div>
      </div>
    );
  }

  if (purchaseStatus) {
    return (
      <div className="loading-modal">
        <div className="d-flex flex-column align-items-center p-4">
          <i
            className={`fa ${purchaseStatus === "success" ? "fa-check" : "fa-close"} text-white ${
              purchaseStatus === "success" ? "bg-success" : "bg-danger"
            } p-3 mb-3`}
            style={{ borderRadius: "50%" }}
          ></i>
          <small className="text-secondary">Pembayaran {textIcon} sebesar</small>
          <h5 className="h5">Rp. {serviceFee.toLocaleString("id-ID")}</h5>
          <small className="text-secondary mb-4">
            {purchaseStatus === "success" ? "Berhasil" : "Gagal"}
          </small>
          <div className="gap-3 d-flex flex-column">
            <h6 className="text-secondary small" style={{ cursor: "pointer" }} onClick={onNavigate}>
              Kembali ke Beranda
            </h6>
          </div>
        </div>
      </div>
    );
  }

  return null;
}

export default PurchasePage;
