import React, { createContext, useState, useEffect, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { baseUrl } from "../api";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [token, setToken] = useState(localStorage.getItem("token") || null);
  const [saldo, setSaldo] = useState(0);
  const [profile, setProfile] = useState({
    email: "",
    namaDepan: "",
    namaBelakang: "",
    profilePicture: "",
  });
  const navigate = useNavigate();

  const login = (newToken) => {
    localStorage.setItem("token", newToken);
    setToken(newToken);
    navigate("/homePage");
  };

  const logout = () => {
    localStorage.removeItem("token");
    setToken(null);
    setSaldo(null);
    setProfile({
      email: null,
      namaDepan: null,
      namaBelakang: null,
      profilePicture: null,
    });
  };

  useEffect(() => {
    if (!token) {
      navigate("/");
    }
  }, [token, navigate]);

  const loadData = useCallback(
    async (currentToken) => {
      try {
        const [balanceResponse, profileResponse] = await Promise.all([
          axios.get(baseUrl + `balance`, {
            headers: {
              Authorization: `Bearer ${currentToken || token}`,
            },
          }),
          axios.get(baseUrl + `profile`, {
            headers: {
              Authorization: `Bearer ${currentToken || token}`,
            },
          }),
        ]);
        if (balanceResponse.data.status === 0) {
          setSaldo(balanceResponse.data.data.balance);
        }
        if (profileResponse.data.status === 0) {
          setProfile({
            email: profileResponse.data.data.email,
            namaDepan: profileResponse.data.data.first_name,
            namaBelakang: profileResponse.data.data.last_name,
            profilePicture: profileResponse.data.data.profile_image,
          });
        }
      } catch (err) {
        console.log(err.response?.data?.message || "Terjadi kesalahan");
        if (err.response && err.response.status === 401) {
          logout();
        }
      }
    },
    [token]
  );

  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      setToken(storedToken);
      loadData(storedToken);
    }
  }, [loadData]);

  useEffect(() => {
    const checkTokenExpiration = () => {
      if (token) {
        const payload = JSON.parse(atob(token.split(".")[1]));
        const isExpired = Date.now() >= payload.exp * 1000;
        if (isExpired) logout();
      }
    };

    checkTokenExpiration();
    const interval = setInterval(checkTokenExpiration, 60000);
    return () => clearInterval(interval);
  }, [token]);

  return (
    <AuthContext.Provider value={{ token, login, logout, saldo, profile, loadData }}>
      {children}
    </AuthContext.Provider>
  );
};
