import React, { useContext, useEffect, useState } from "react";
import { Header, InfoUserSaldo } from "../components";
import axios from "axios";
import { baseUrl } from "../api";
import { AuthContext } from "../contexts/AuthContext";

function TransaksiPage() {
  const { token } = useContext(AuthContext);
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [limit, setLimit] = useState(5);

  const loadHistory = async () => {
    setLoading(true);
    try {
      const response = await axios.get(
        `${baseUrl}transaction/history?offset=0&limit=${limit}`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (response.data.status === 0) {
        setHistory(response.data.data.records);
      }
    } catch (error) {
      console.log(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadHistory();
  }, [limit, token]);

  return (
    <div className="container-fluid vh-100">
      <Header />
      <div className="container mt-5">
        <InfoUserSaldo />
        <div className="mt-5 container-fluid w-100 p-0">
          <h4 className="mb-4">Semua Transaksi</h4>
          {history.map((transactionHistory) => (
            <div className="card p-3 mb-4">
              <div className="d-flex justify-content-between align-items-center">
                <h5>Rp. {parseInt(transactionHistory.total_amount).toLocaleString("id-ID")}</h5>
                <h6>{transactionHistory.description}</h6>
              </div>
              <small>{transactionHistory.created_on}</small>
            </div>
          ))}
          <h6
            className="text-danger small text-center btn w-100"
            onClick={() => {
              setLimit(limit + 5);
            }}
          >
            Show More
          </h6>
        </div>
      </div>
      {loading && (
        <div className="loading-modal">
          <div className="loading-spinner">
            <div className="spinner-border text-danger" role="status">
              <span className="visually-hidden">Loading...</span>
            </div>
            <p>Loading Transaction History...</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default TransaksiPage;
