import React, { useState } from "react";
import { Header, InfoUserSaldo, InputField } from "../components";
import { useLocation } from "react-router-dom";

function PurchasePage() {
  const location = useLocation()
  const { icons, textIcon, serviceFee, serviceCode } = location.state || {}
  const [purchase, setPurchase] = useState(0);
  const handleInputChange = (e) => {
    const value = e.target.value.replace(/[^0-9]/g, "");
    setPurchase(value);
  };

  const validation =
    parseInt(purchase) >= 10000 && parseInt(purchase) <= 1000000;

  return (
    <div className="container-fluid vh-100">
      <Header />
      <div className="container mt-5">
        <InfoUserSaldo />
        <div className="mt-5 container-fluid w-100 p-0">
          <h4 className="mb-5">Pembayaran</h4>
          <div className="d-flex gap-4 mb-4">
            <img src={icons} /> <h5>{textIcon}</h5>
          </div>
          <div className="w-100">
            <div className="mb-3">
              <InputField
                type="text"
                iconClass="fa fa-money"
                value={purchase ? parseInt(purchase).toLocaleString() : ""}
                onChange={handleInputChange}
                placeholder="masukkan nominal pembayaran"
                isValid={!validation}
                errorMessage={!validation ? "Pem" : ""}
              />
            </div>
            <button className="btn btn-danger w-100" disabled={!validation}>
              Top Up
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PurchasePage;
