import React, { useContext, useState } from "react";
import { Header, InfoUserSaldo, InputField } from "../components";
import { useLocation } from "react-router-dom";
import axios from "axios";
import { baseUrl } from "../api";
import { AuthContext } from "../contexts/AuthContext";

function PurchasePage() {
  const {token} = useContext(AuthContext)
  const location = useLocation()
  const { icons, textIcon, serviceFee, serviceCode } = location.state || {}
  const [purchase, setPurchase] = useState(0);
  const [loading, setLoading] = useState(false);
  const [purchaseStatus, setPurchaseStatus] = useState(false);
  const [confirmPurchase, setConfirmPurchase] = useState(false);
  
  const handleInputChange = (e) => {
    const value = e.target.value.replace(/[^0-9]/g, "");
    setPurchase(value);
  };

  const validation =
    parseInt(purchase) >= 1000;

  const purchaseAction = async () => {
    setConfirmPurchase(true)
    // try {
    //   const response = await axios.post(`${baseUrl}transaction`, {
    //     service_code: serviceCode
    //   }, {
    //     headers: {
    //       Authorization: `Bearer ${token}`
    //     }
    //   })

    //   if(response.data.status === 0) {

    //   }
    // } catch (error) {

    // } finally {
    //   setLoading(false)
    // }
  }

  return (
    <div className="container-fluid vh-100">
      <Header />
      <div className="container mt-5">
        <InfoUserSaldo />
        <div className="mt-5 container-fluid w-100 p-0">
          <h4 className="mb-3">Pembayaran</h4>
          <div className="d-flex gap-3 mb-5 align-items-center">
            <img src={icons} className="img-fluid" alt={textIcon} style={{maxWidth: 30}} /> <h5>{textIcon}</h5>
          </div>
          <div className="w-100">
            <div className="mb-3">
              <InputField
                type="text"
                iconClass="fa fa-money"
                value={serviceFee ? parseInt(serviceFee).toLocaleString() : ''}
                onChange={handleInputChange}
                placeholder="masukkan nominal pembayaran"
                isValid={validation}
                errorMessage={validation ? "Pem" : ""}
              />
            </div>
            <button className="btn btn-danger w-100" disabled={validation} onClick={purchaseAction}>
              Top Up
            </button>
          </div>
        </div>
      </div>
      {(loading || confirmPurchase || purchaseStatus) && (
        <div className="loading-modal">
            <div className="loading-spinner">
          {loading && (
            <>
              <div className="spinner-border text-danger" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p>Your purchase is processing...</p>
              </>
            )}
            </div>
          {confirmPurchase && (
            <div className="d-flex align-items-center justify-content-center">
              <i className="fa fa-wallet"></i>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default PurchasePage;
