import React from "react";

function InputField({
  type,
  placeholder,
  iconClass,
  value,
  onChange,
  seePassword,
  visible,
}) {
  return (
    <div className="form-control mb-3 d-flex align-items-center">
      <i className={`${iconClass} me-3 text-secondary`}></i>
      <input
        type={type}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        style={{
          border: "none",
          width: "100%",
          outline: "none",
          boxShadow: "none",
        }}
      />
      {type === "password" && seePassword && (
        <i
          className={`fa ${
            visible ? "fa fa-eye" : "fa fa-eye-eye"
          } ms-2 text-secondary`}
          onClick={seePassword}
        ></i>
      )}
    </div>
  );
}

export default InputField;
