import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import {
  Banner1,
  Banner2,
  Banner3,
  Banner4,
  Banner5,
  GameIcon,
  ListrikIcon,
  MusikIcon,
  PaketDataIcon,
  PBBIcon,
  PDAMIcon,
  PGNIcon,
  PulsaIcon,
  QurbanIcon,
  TevelisiIcon,
  VoucherMakananIcon,
  ZakatIcon,
} from "../assets/icon";
import { HomeIcons, Header, InfoUserSaldo } from "../components";
import axios from "axios";
import { baseUrl } from "../api";

function HomePage() {
  const [services, setServices] = useState([]);
  const [banners, setBanners] = useState([])

  useEffect(() => {
    const loadService = async () => {
      try {
        const response = await axios.get(baseUrl + `services`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        if (response.data.status === 0) {
          setServices(response.data.data);
        }
      } catch (error) {
        console.log(error.response?.data?.message || "Terjadi kesalahan");
      } finally {
      }
    };
    loadService();
  });

  useEffect(() => {
    const loadBanner = async () => {
      try {
        const response = await axios.get(baseUrl + 'banner', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("Token")}`
          }
        })

        if(response.data.status === 0) {
          setBanners(response.data.data)
        }
      } catch (err) {
        console.log(err.response?.data?.message || "Terjadi kesalahan")
      } finally {

      }
    }
  })
  return (
    <div className="container-fluid vh-100">
      <Header />
      <div className="container mt-5">
        <InfoUserSaldo />
        <div className="d-flex justify-content-between mt-5">
          {services.map((serviceData) => (
            <HomeIcons
              icons={serviceData.service_icon}
              alt={serviceData.service_name}
              textIcon={serviceData.service_name}
            />
          ))}
        </div>
        <div
          className="container-fluid p-0 my-3"
          style={{ overflow: "hidden" }}
        >
          <h6 className="h6 mb-4">Temukan Promo Menarik</h6>
          <div
            className="d-flex gap-4 custom-scroll"
            style={{ overflowX: "auto", whiteSpace: "nowrap", padding: 8 }}
          >
            {}
            <img src={Banner1} className="img-fluid" alt="Banner Promo 1" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
