import React, { useEffect, useState } from 'react'
import { Header, InfoUserSaldo } from '../components'
import axios from 'axios'
import { baseUrl } from '../api'

function TransaksiPage() {
  const {token} = use
  const [history, setHistory] = useState([])
  const [loading, setLoading] = useState(false)
  let limit = 5

  useEffect(() => {
    const loadHistory = async () => {
      setLoading(true)
      try {
        const response = await axios.get(`${baseUrl}transaction/history?offset=0&limit=${limit}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        })
      } catch (error){
        console.log(error)
      } finally {
        setLoading(false)
      }
    }
  })
  return (
    <div className='container-fluid vh-100'>
      <Header />
      <div className='container mt-5'>
        <InfoUserSaldo />
        <div className='mt-5 container-fluid w-100 p-0'>
          <h4 className='mb-4'>Semua Transaksi</h4>
          <div className='card p-3'>
            <div className='d-flex justify-content-between align-items-center'>
            <h5>asbkdjavbsbv</h5>
            <h6>asbkdjavbsbv</h6>
            </div>
            <small>asbkdjavbsbv</small>
          </div>
        </div>
      </div>
    </div>
  )
}

export default TransaksiPage
