import React from "react";

function InputField({
  type,
  placeholder,
  iconClass,
  value,
  onChange,
  togglePassword,
  showPassword,
  isInvalid, // terima properti isInvalid
}) {
  return (
    <div
      className="form-control mb-3 d-flex align-items-center"
      style={{
        borderColor: isInvalid ? "#ff3b3b" : "inherit", // ubah warna border jika invalid
      }}
    >
      <i
        className={`${iconClass} me-3`}
        style={{ color: isInvalid ? "#ff3b3b" : "inherit" }} // ubah warna ikon jika invalid
      ></i>
      <input
        type={type}
        placeholder={placeholder}
        value={value}
        onChange={onChange}
        style={{
          border: "none",
          width: "100%",
          outline: "none",
          boxShadow: "none",
          color: isInvalid ? "#ff3b3b" : "inherit", // ubah warna teks jika invalid
        }}
      />
      {type === "password" && togglePassword && (
        <i
          className={`fa ${
            showPassword ? "fa-eye-slash" : "fa-eye"
          } ms-2`}
          onClick={togglePassword}
          style={{
            cursor: "pointer",
            color: isInvalid ? "#ff3b3b" : "inherit", // ubah warna ikon toggle jika invalid
          }}
        ></i>
      )}
    </div>
  );
}

export default InputField;
